import React, { useState } from "react";
import "../Css/trangchu.css";

function Trangchu() {
    const [loggedIn, setLoggedIn] = useState(false);
    const [flights, setFlights] = useState([]);

    const login = () => {
        setLoggedIn(true);
        displayPopularFlights();
    };

    const logout = () => {
        setLoggedIn(false);
        setFlights([]);
    };

    const displayPopularFlights = () => {
        const popularFlights = [
            { route: "SGN-HAN", date: "15/11/2024", price: "1.500.000 VNĐ" },
            { route: "HAN-SGN", date: "16/11/2024", price: "1.500.000 VNĐ" },
            { route: "SGN-DAD", date: "17/11/2024", price: "1.200.000 VNĐ" },
            { route: "SGN-PQC", date: "18/11/2024", price: "1.300.000 VNĐ" },
            { route: "HAN-CXR", date: "19/11/2024", price: "1.400.000 VNĐ" },
        ];
        setFlights(popularFlights);
    };

    const searchFlights = () => {
        const departure = document.getElementById("departure").value;
        const destination = document.getElementById("destination").value;
        const departureDate = document.getElementById("departure-date").value;

        if (!departure || !destination || !departureDate) {
            alert("Vui lòng chọn nơi khởi hành, nơi đến và ngày khởi hành.");
            return;
        }

        const allFlights = [
            { route: "HAN-SGN", date: departureDate, price: "1.500.000 VNĐ" },
            { route: "SGN-DAD", date: departureDate, price: "1.200.000 VNĐ" },
            { route: "DAD-HAN", date: departureDate, price: "1.400.000 VNĐ" },
            { route: "PQC-CXR", date: departureDate, price: "1.300.000 VNĐ" },
            { route: "HAN-PQC", date: departureDate, price: "1.700.000 VNĐ" },
        ];

        const matchingFlights = allFlights.filter(
            (flight) =>
                (flight.route === `${departure}-${destination}` ||
                    flight.route === `${destination}-${departure}`) &&
                flight.date === departureDate
        );

        if (matchingFlights.length === 0) {
            setFlights([{ message: "Không có chuyến bay nào cho lựa chọn của bạn." }]);
        } else {
            setFlights(matchingFlights);
        }
    };

    return (
        <div>
            <header>
                <h1>AirPass</h1>
                <p>Chọn điểm đến và đặt vé dễ dàng</p>
                <div className="user-info" id="user-info">
                    {loggedIn ? (
                        <>
                            <span>Xin chào, Khách hàng</span>
                            <button onClick={logout}>Đăng Xuất</button>
                        </>
                    ) : (
                        <button onClick={login}>Đăng Nhập</button>
                    )}
                </div>
            </header>
            
            <main>
                <section className="destination-selection">
                    <h2>Chọn Điểm Đến</h2>
                    <form onSubmit={(e) => { e.preventDefault(); searchFlights(); }}>
                        <label htmlFor="departure">Nơi khởi hành:</label>
                        <select id="departure">
                            <option value="">Chọn sân bay khởi hành</option>
                            <option value="HAN">Hà Nội - Nội Bài (HAN)</option>
                            <option value="SGN">TP. Hồ Chí Minh - Tân Sơn Nhất (SGN)</option>
                            <option value="DAD">Đà Nẵng - Đà Nẵng (DAD)</option>
                            <option value="PQC">Phú Quốc - Phú Quốc (PQC)</option>
                            <option value="CXR">Cam Ranh - Cam Ranh (CXR)</option>
                        </select>

                        <label htmlFor="destination">Nơi đến:</label>
                        <select id="destination">
                            <option value="">Chọn sân bay nơi đến</option>
                            <option value="HAN">Hà Nội - Nội Bài (HAN)</option>
                            <option value="SGN">TP. Hồ Chí Minh - Tân Sơn Nhất (SGN)</option>
                            <option value="DAD">Đà Nẵng - Đà Nẵng (DAD)</option>
                            <option value="PQC">Phú Quốc - Phú Quốc (PQC)</option>
                            <option value="CXR">Cam Ranh - Cam Ranh (CXR)</option>
                        </select>

                        <label htmlFor="departure-date">Ngày khởi hành:</label>
                        <input type="date" id="departure-date" />

                        <button type="submit">Tìm chuyến bay</button>
                    </form>
                </section>

                <section className="flight-list" id="flight-list">
                    <h2>Các chuyến bay đề xuất</h2>
                    <ul>
                        {flights.length > 0 ? (
                            flights.map((flight, index) => (
                                <li key={index}>
                                    {flight.route} - Ngày: {flight.date} - Giá: {flight.price}
                                </li>
                            ))
                        ) : (
                            <li>{flights.length === 1 ? flights[0].message : "Không có chuyến bay nào."}</li>
                        )}
                    </ul>
                </section>
            </main>

            <footer>
                <h2>Thanh toán</h2>
                <button>Thanh toán qua Momo</button>
                <button>Thanh toán qua Visa</button>
                <button>Thanh toán qua Ngân hàng</button>
            </footer>
        </div>
    );
}

export default Trangchu;
