import React, {Component} from "react";

const users = [
  {
    id:1,
    name:"Tuan Hung",
  },
  {
    id:2,
    name:"Le Nhien",
  },
  {
    id:3,
    name:"Bao Nguyen",
  },
  {
    id:4,
    name:"Bao Ngoc",
  }
];

export class Users extends Component {
  constructor() {
    super();
    this.state = {
      users: [],
    }
  }

  handleload = () => {
    this.setState({
      users: users,
    });
  }

  render() {
    const {users} = this.state;
    console.log(users); 
    return (
      <div>
        <h1>Đây là demo ReactJS</h1>
        <button type="button" onClick={this.handleload}>Chạy</button>
         {/* Hiển thị dữ liệu users lên màn hình */}
      <ul>
        {users && users.length > 0 ? (
          users.map((user, index) => (
            <li key={index}>{user.name}</li>  // Hiển thị tên của mỗi user
          ))
        ) : (
          <li>Không có người dùng nào</li>  // Hiển thị khi mảng rỗng
        )}
      </ul>
      </div>
    );
  }
}

export default Users
