import React, { useState } from "react";
import "../Css/trangchu.css";

function Trangchu() {
    const [cart, setCart] = useState([]);
    const [activeSection, setActiveSection] = useState("flight-form");

    const searchFlights = (departure, destination, departureDate) => {
        if (!departure || !destination || !departureDate) {
            alert("Vui lòng chọn nơi khởi hành, nơi đến và ngày khởi hành.");
            return;
        }
        // Thực hiện tìm kiếm chuyến bay nếu cần
    };

    const showSection = (sectionId) => {
        setActiveSection(sectionId);
    };

    const updateCart = () => {
        return cart.map((item, index) => (
            <div key={index} className="cart-item">
                <p>Nơi khởi hành: {item.from}</p>
                <p>Nơi đến: {item.to}</p>
                <p>Ngày khởi hành: {item.date}</p>
                <p>Giờ khởi hành: {item.time}</p>
                <p>Số lượng khách hàng: {item.passengers}</p>
                <p>Tổng giá vé: {(item.price * item.passengers).toLocaleString()} VND</p>
            </div>
        ));
    };

    return (
        <div>
            <div className="header">
                <div className="logo">
                    <h1>AirPass</h1>
                </div>
            </div>
            <div className="container">
                <h2>Đặt Vé Máy Bay</h2>
                <div className="main-buttons">
                    <button className="main-button" onClick={() => showSection('flight-form')}>Chuyến bay</button>
                    <button className="main-button" onClick={() => showSection('suggested-flights')}>Các chuyến bay đề xuất</button>
                    <button className="main-button" onClick={() => showSection('payment-section')}>Giỏ hàng</button>
                </div>

                {activeSection === 'flight-form' && (
                    <div id="flight-form" className="content-section active">
                        <div className="flight-form">
                            <div className="form-group">
                                <label>Nơi khởi hành</label>
                                <select id="departure">
                                    <option value="hcm">Hồ Chí Minh</option>
                                    <option value="hn">Hà Nội</option>
                                </select>
                            </div>
                            <div className="form-group">
                                <label>Nơi đến</label>
                                <select id="destination">
                                    <option value="dn">Đà Nẵng</option>
                                </select>
                            </div>
                            <div className="form-group">
                                <label>Ngày khởi hành</label>
                                <input type="date" id="departure-date" />
                            </div>
                            <button onClick={() => searchFlights(
                                document.getElementById("departure").value,
                                document.getElementById("destination").value,
                                document.getElementById("departure-date").value
                            )}>Tìm chuyến bay</button>
                        </div>
                    </div>
                )}

                {activeSection === 'suggested-flights' && (
                    <div id="suggested-flights" className="content-section">
                        {/* Hiển thị chuyến bay đề xuất */}
                    </div>
                )}

                {activeSection === 'payment-section' && (
                    <div id="payment-section" className="content-section">
                        <h3>Thông tin vé</h3>
                        <div id="cart-items">
                            {cart.length > 0 ? updateCart() : <p>Chưa có vé</p>}
                        </div>
                        <h3>Thanh toán</h3>
                        <div className="payment-buttons">
                            <button className="payment-button">Momo</button>
                            <button className="payment-button">Visa</button>
                            <button className="payment-button">Ngân hàng</button>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
}

export default Trangchu;
