import React, { useState } from 'react';
import styles from '../Css/trangchu.module.css';

function Trangchu() {
    const [cart, setCart] = useState([]);
    const [activeSection, setActiveSection] = useState('bookTicket');

    const showSection = (sectionId) => {
        setActiveSection(sectionId);
        if (sectionId === 'cart') {
            updateCart();
        }
    };

    const addToCart = (item) => {
        setCart([...cart, item]);
        alert(`${item} đã được thêm vào giỏ hàng!`);
        updateCart();
    };

    const updateCart = () => {
        // Update cart logic can be defined here
    };

    const removeFromCart = (index) => {
        const newCart = cart.filter((_, i) => i !== index);
        setCart(newCart);
        alert("Đã xóa khỏi giỏ hàng!");
    };

    return (
        <div className={styles.container}>
            {/* Header */}
            <div className={styles.header}>
                <div className={styles.logo}>AirPass</div>
                <button className={styles.loginButton} onClick={() => alert("Đăng Nhập Clicked!")}>
                    Đăng Nhập
                </button>
            </div>

            {/* Navigation */}
            <div className={styles.nav}>
                <button className={styles.navButton} onClick={() => showSection('bookTicket')}>Mua Vé</button>
                <button className={styles.navButton} onClick={() => showSection('tourList')}>Tour</button>
                <button className={styles.navButton} onClick={() => showSection('cart')}>Giỏ Hàng</button>
            </div>

            {/* Mua Vé */}
            {activeSection === 'bookTicket' && (
                <div className={styles.section} id="bookTicket">
                    <h2 className={styles.sectionTitle}>MUA VÉ MÁY BAY</h2>
                    <div className={styles.searchBox}>
                        <label htmlFor="departure">Nơi đi:</label>
                        <select id="departure">
                            <option value="SGN">Tp. Hồ Chí Minh (SGN)</option>
                            <option value="HAN">Hà Nội (HAN)</option>
                            <option value="DAD">Đà Nẵng (DAD)</option>
                            <option value="CXR">Nha Trang (CXR)</option>
                        </select>

                        <label htmlFor="destination">Nơi đến:</label>
                        <select id="destination">
                            <option value="SGN">Tp. Hồ Chí Minh (SGN)</option>
                            <option value="HAN">Hà Nội (HAN)</option>
                            <option value="DAD">Đà Nẵng (DAD)</option>
                            <option value="CXR">Nha Trang (CXR)</option>
                        </select>

                        <label htmlFor="departureDate">Ngày đi:</label>
                        <input type="date" id="departureDate" />

                        <label htmlFor="passengerCount">Số lượng khách:</label>
                        <input type="number" id="passengerCount" min="1" defaultValue="1" />

                        <button className={styles.addToCartButton} onClick={() => addToCart('Vé máy bay')}>Bỏ vào giỏ hàng</button>
                    </div>
                </div>
            )}

            {/* Tour List */}
            {activeSection === 'tourList' && (
                <div className={styles.section} id="tourList">
                    <h2 className={styles.sectionTitle}>DANH SÁCH TOUR</h2>
                    {/* Tour Items */}
                    {["Phú Quốc", "Hạ Long", "Nha Trang"].map(tour => (
                        <div className={styles.tourItem} key={tour}>
                            <div>
                                <h3>DU LỊCH {tour.toUpperCase()} - THIÊN ĐƯỜNG GIẢI TRÍ</h3>
                                <p className={styles.details}>Thông tin chi tiết về tour {tour}.</p>
                            </div>
                            <div>
                                <span className={styles.price}>Giá từ 8.079.000 VNĐ</span>
                                <button className={styles.addToCartButton} onClick={() => addToCart(`Tour ${tour}`)}>Bỏ vào giỏ hàng</button>
                            </div>
                        </div>
                    ))}
                </div>
            )}

            {/* Giỏ Hàng */}
            {activeSection === 'cart' && (
                <div className={styles.section} id="cart">
                    <h2 className={styles.sectionTitle}>GIỎ HÀNG</h2>
                    <div id="cartContent">
                        {cart.length > 0 ? (
                            cart.map((item, index) => (
                                <div className={styles.cartInfo} key={index}>
                                    <p>{item}</p>
                                    <button className={styles.removeButton} onClick={() => removeFromCart(index)}>Xóa</button>
                                </div>
                            ))
                        ) : (
                            <p className={styles.emptyCart}>Hiện chưa có vé</p>
                        )}
                    </div>
                    {cart.length > 0 && (
                        <div id="paymentOptions">
                            <button className={styles.paymentButton}>Momo</button>
                            <button className={styles.paymentButton}>Visa</button>
                            <button className={styles.paymentButton}>Ngân Hàng</button>
                        </div>
                    )}
                </div>
            )}
        </div>
    );
}

export default Trangchu;
