import React, { useState, useContext } from "react";
import "../Css/dangnhap.css";
import { Link } from "react-router-dom";
import { FormContext } from "../Context/FormContext";


function Dangnhap() {
  const { formData, setFormData } = useContext(FormContext);

  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [rememberMe, setRememberMe] = useState(false);

  const handleLogin = (e) => {
    e.preventDefault();
    // Gửi request đăng nhập đến server tại đây
    console.log("Username:", username);
    console.log("Password:", password);
    console.log("Remember Me:", rememberMe);
    // Xử lý đăng nhập và chuyển hướng nếu thành công
  };

  return (
    <div className="login-container">
      <h2>Đăng nhập</h2>
      <form onSubmit={handleLogin}>
        <input
          type="text"
          name="username"
          placeholder="Tên đăng nhập"
          value={formData.customerName} // Sử dụng formData
          onChange={(e) => setFormData({ ...formData, customerName: e.target.value })}
          required
        />
        <input
          type="password"
          name="password"
          placeholder="Mật khẩu"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <div style={{ display: "flex", alignItems: "center", margin: "10px 0" }}>
          <input
            type="checkbox"
            id="remember_me"
            checked={rememberMe}
            onChange={(e) => setRememberMe(e.target.checked)}
          />
          <label htmlFor="remember_me" style={{ marginLeft: "5px", fontSize: "14px" }}>
            Nhớ mật khẩu
          </label>
        </div>
        <button type="submit" className="login-button">Đăng nhập</button>
      </form>
      <p>Bạn chưa có tài khoản? <Link to="DangKy">Đăng ký ngay</Link></p>
      <p><Link to="QuenMatKhau">Quên mật khẩu</Link></p>
    </div>
  );
}

export default Dangnhap;
