import React, { useState } from 'react';
import '../Css/trangchu.css';

function Trangchu() {
    const [cart, setCart] = useState([]);
    const [activeSection, setActiveSection] = useState('bookTicket');

    const showSection = (sectionId) => {
        setActiveSection(sectionId);
        if (sectionId === 'cart') {
            updateCart();
        }
    };

    const addToCart = (item) => {
        setCart([...cart, item]);
        alert(`${item} đã được thêm vào giỏ hàng!`);
        updateCart();
    };

    const updateCart = () => {
        // Hàm cập nhật giỏ hàng có thể được định nghĩa ở đây
    };

    const removeFromCart = (index) => {
        const newCart = cart.filter((_, i) => i !== index);
        setCart(newCart);
        alert("Đã xóa khỏi giỏ hàng!");
    };

    return (
        <div>
            {/* Header */}
            <div className="header">
                <div className="logo">AirPass</div>
                <button className="login-button" onClick={() => alert("Đăng Nhập Clicked!")}>
                    Đăng Nhập
                </button>
            </div>

            {/* Navigation */}
            <div className="nav">
                <button onClick={() => showSection('bookTicket')}>Mua Vé</button>
                <button onClick={() => showSection('tourList')}>Tour</button>
                <button onClick={() => showSection('cart')}>Giỏ Hàng</button>
            </div>

            {/* Sections */}
            {activeSection === 'bookTicket' && (
                <div className="section">
                    <div className="search-box">
                        <label htmlFor="departure">Nơi đi:</label>
                        <select id="departure">
                            <option value="SGN">Tp. Hồ Chí Minh (SGN)</option>
                            <option value="HAN">Hà Nội (HAN)</option>
                            <option value="DAD">Đà Nẵng (DAD)</option>
                            <option value="CXR">Nha Trang (CXR)</option>
                        </select>

                        <label htmlFor="destination">Nơi đến:</label>
                        <select id="destination">
                            <option value="SGN">Tp. Hồ Chí Minh (SGN)</option>
                            <option value="HAN">Hà Nội (HAN)</option>
                            <option value="DAD">Đà Nẵng (DAD)</option>
                            <option value="CXR">Nha Trang (CXR)</option>
                        </select>

                        <label htmlFor="departureDate">Ngày đi:</label>
                        <input type="date" id="departureDate" />

                        <label htmlFor="passengerCount">Số lượng khách:</label>
                        <input type="number" id="passengerCount" min="1" defaultValue="1" />

                        <button onClick={() => addToCart('Vé máy bay')}>Bỏ vào giỏ hàng</button>
                    </div>
                </div>
            )}

            {activeSection === 'tourList' && (
                <div className="section">
                    <div className="tour-item">
                        <h3>DU LỊCH PHÚ QUỐC - THIÊN ĐƯỜNG GIẢI TRÍ</h3>
                        <p className="details">
                            TP. HỒ CHÍ MINH - PHÚ QUỐC <br />
                            Thời gian: 3 ngày 2 đêm <br />
                            Phương tiện: Hàng không Vietnam Airlines, Pacific Airlines
                        </p>
                        <span className="price">Giá từ 8.079.000</span>
                        <button onClick={() => addToCart('Tour Phú Quốc')}>Bỏ vào giỏ hàng</button>
                    </div>
                    {/* Các tour khác */}
                </div>
            )}

            {activeSection === 'cart' && (
                <div className="section">
                    <div id="cartContent">
                        {cart.length > 0 ? (
                            cart.map((item, index) => (
                                <div className="cart-info" key={index}>
                                    <p>{item}</p>
                                    <p>Chi tiết vé</p>
                                    <button onClick={() => removeFromCart(index)}>Xóa</button>
                                </div>
                            ))
                        ) : (
                            <p className="empty-cart">Hiện chưa có vé</p>
                        )}
                    </div>
                    {cart.length > 0 && (
                        <div id="paymentOptions">
                            <button className="payment-button">Momo</button>
                            <button className="payment-button">Visa</button>
                            <button className="payment-button">Ngân Hàng</button>
                        </div>
                    )}
                </div>
            )}
        </div>
    );
}

export default Trangchu;
