import React, { useState, useContext, useEffect } from "react";
import "../Css/dangnhap.css";
import { Link } from "react-router-dom";
import { FormContext } from "../Context/FormContext";
import maybayImage from '../image/maybay.jpg';
import googleLogo from '../image/gmail.jpg';
import facebookLogo from '../image/facebook.jpg';

function Dangnhap() {
  const { formData, setFormData } = useContext(FormContext);
  const [password, setPassword] = useState("");
  const [rememberMe, setRememberMe] = useState(false);

  const handleLogin = (e) => {
    e.preventDefault();
    console.log("Phone:", formData.Phone);
    console.log("Password:", password);
    console.log("Remember Me:", rememberMe);
    // Thêm logic đăng nhập ở đây
  };

  // useEffect(() => {
  //   fetch('/auth/google')
  //     .then(response => response.json())
  //     .then(data => console.log(data))
  //     .catch(error => console.error('Error:', error));
  // }, []);

  return (
    <div className="login-container">
      <img src={maybayImage} alt="Airplane" />
      <h2>Đăng nhập</h2>
      <form onSubmit={handleLogin}>
        <input
          type="text"
          name="phone"
          placeholder="Số điện thoại"
          value={formData.Phone}
          onChange={(e) => setFormData({ ...formData, Phone: e.target.value })}
          required
        />
        <input
          type="password"
          name="password"
          placeholder="Mật khẩu"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <div style={{ display: "flex", alignItems: "center", margin: "10px 0" }}>
          <input
            type="checkbox"
            id="remember_me"
            checked={rememberMe}
            onChange={(e) => setRememberMe(e.target.checked)}
          />
          <label htmlFor="remember_me" style={{ marginLeft: "5px", fontSize: "14px" }}>
            Nhớ mật khẩu
          </label>
        </div>
        <button type="submit" className="login-button">Đăng nhập</button>
      </form>
      <p>Bạn chưa có tài khoản? <Link to="/DangKy">Đăng ký ngay</Link></p>
      <p><Link to="/QuenMatKhau">Quên mật khẩu</Link></p>
      <div className="social-login">
        <button className="social-button google-button"  onClick={() => window.open("http://localhost:5000/auth/google", "_self") }>
          <img src={googleLogo} alt="Google Logo" /> Đăng nhập với Google
        </button>
        <button className="social-button facebook-button">
          <img src={facebookLogo} alt="Facebook Logo" /> Đăng nhập với Facebook
        </button>
      </div>
    </div>
  );
}

export default Dangnhap;
